export declare const typeDefs = "\n  type Pizza {\n    dough: String!\n    toppings: [String!]\n  }\n\n  type Salad {\n    ingredients: [String!]!\n  }\n\n  union Food = Pizza | Salad\n\n  type Book {\n    id: ID!\n    title: String!\n    type: BookType!\n  }\n\n  enum BookType {\n    AUDIO\n    LEGACY\n  }\n\n  type User {\n    id: ID!\n    name: String!\n    favoritePizza: Pizza!\n    favoriteBook: Book!\n    favoriteFood: Food!\n    shelf: [Book!]!\n  }\n\n  type Post {\n    comments(filter: String!): [String!]\n  }\n\n  type Query {\n    \"\"\"\n    Resolves current user\n    \"\"\"\n    me: User\n    user(id: ID!): User\n    users: [User!]\n    usersLimit(limit: Int!): [User!]\n    usersSort(sort: Boolean!): [User!]\n    book(id: ID!): Book\n    books: [Book!]\n    never: String\n    feed: [Post]\n  }\n\n  type Mutation {\n    addBook(title: String!): Book\n  }\n\n  type Subscription {\n    onBook: Book\n  }\n\n  schema {\n    query: Query\n    mutation: Mutation\n    subscription: Subscription\n  }\n";
